		public int tirarDados6() {
			int mesPetit = 0;
			int cares = 6;
			int suma = 0;
			for (int i = 0; i < 4; i++) {
				int actual = (int) Math.floor(Math.random() * (cares - 1 + 1)) + 1;
				if (mesPetit == 0 || mesPetit > actual)
					mesPetit = actual;
				suma += actual;
			}
			suma -= mesPetit;
			return suma;
		}

